mapper.update:
UPDATE comment SET
co_subject = #{co_subject}, co_content = #{co_content} 
WHERE co_uid = #{co_uid}

mapper.select:
	
 			SELECT * FROM COMMENT c INNER JOIN MEMBER m 
			ON c.mb_uid = m.mb_uid
			WHERE c.g_uid = #{g_uid}
			ORDER BY c.co_regdate DESC
			LIMIT #{fromRow}, #{writePages}
